var gulp=require("gulp"),nunjucksRender=require("gulp-nunjucks-render");gulp.task("nunjucks_one",function(){return gulp.src("templates/blog/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["templates/blog/templates"]})).pipe(gulp.dest("templates/blog"))}),gulp.task("nunjucks_two",function(){return gulp.src("templates/portfolio/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["templates/portfolio/templates"]})).pipe(gulp.dest("templates/portfolio"))}),gulp.task("nunjucks_three",function(){return gulp.src("templates/product/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["templates/product/templates"]})).pipe(gulp.dest("templates/product"))}),gulp.task("nunjucks",["nunjucks_one","nunjucks_two","nunjucks_three"]);