var gulp=require("gulp"),nunjucksRender=require("gulp-nunjucks-render");gulp.task("nunjucks_one",function(){return gulp.src("boilerplate/blog/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["boilerplate/blog/templates"]})).pipe(gulp.dest("boilerplate/blog"))}),gulp.task("nunjucks_two",function(){return gulp.src("boilerplate/portfolio/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["boilerplate/portfolio/templates"]})).pipe(gulp.dest("boilerplate/portfolio"))}),gulp.task("nunjucks_three",function(){return gulp.src("boilerplate/product/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["boilerplate/product/templates"]})).pipe(gulp.dest("boilerplate/product"))}),gulp.task("nunjucks",["nunjucks_one","nunjucks_two","nunjucks_three"]);