var gulp=require("gulp"),nunjucksRender=require("gulp-nunjucks-render");gulp.task("nunjucks",function(){return gulp.src("boilerplate/blog/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["boilerplate/blog/templates"]})).pipe(gulp.dest("boilerplate/blog"))}),gulp.task("nunjucks",function(){return gulp.src("boilerplate/portfolio/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["boilerplate/portfolio/templates"]})).pipe(gulp.dest("boilerplate/portfolio"))}),gulp.task("nunjucks",function(){return gulp.src("boilerplate/product/pages/**/*.+(html|nunjucks)").pipe(nunjucksRender({path:["boilerplate/product/templates"]})).pipe(gulp.dest("boilerplate/product"))});